pub enum DrawCommand {
    ClearScreen,
    ClearToColor {
            color: RGB,
        },
    SetTarget {
            console: usize,
        },
    Set {
            pos: Point,
            color: ColorPair,
            glyph: u8,
        },
    SetBackground {
            pos: Point,
            bg: RGB,
        },
    Print {
            pos: Point,
            text: String,
        },
    PrintColor {
            pos: Point,
            text: String,
            color: ColorPair,
        },
    PrintCentered {
            y: i32,
            text: String,
        },
    PrintColorCentered {
            y: i32,
            text: String,
            color: ColorPair,
        },
    Box {
            pos: Rect,
            color: ColorPair,
        },
    HollowBox {
            pos: Rect,
            color: ColorPair,
        },
    DoubleBox {
            pos: Rect,
            color: ColorPair,
        },
    HollowDoubleBox {
            pos: Rect,
            color: ColorPair,
        },
    FillRegion {
            pos: Rect,
            color: ColorPair,
            glyph: u8,
        },
    BarHorizontal {
            pos: Point,
            width: i32,
            n: i32,
            max: i32,
            color: ColorPair,
        },
    BarVertical {
            pos: Point,
            height: i32,
            n: i32,
            max: i32,
            color: ColorPair,
        },
}

pub enum CommandType {
    Text {
            block: Vec<u8>,
        },
    Centered {
            block: Vec<u8>,
        },
    NewLine {
        },
    Foreground {
            col: RGB,
        },
    Background {
            col: RGB,
        },
    TextWrapper {
            block: String,
        },
    Reset {
        },
}
